mapper.select:
select

from ct_order

	
		AND userId = #{userId}
	
	
		AND orderStatus = #{orderStatus}
	
	
		AND payStatus = #{payStatus}

SELECT a.*,b.user_name,b.true_name,b.user_level
FROM ct_order a LEFT
JOIN ct_user b ON a.userid = b.id

	
		AND a.userId = #{userId}
	
	
		AND a.orderStatus = #{orderStatus}
	
	
		AND a.payStatus = #{payStatus}
	
	
		AND a.orderType = #{orderType}
	
	
		AND b.user_name = #{userName}
	
	
		AND a.payTime >= STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')
	
	
		AND a.payTime <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')
	

order by a.createTime

	limit #{offset}, #{rows}

SELECT count(1)
FROM ct_order a LEFT JOIN ct_user b ON a.userid = b.id

	
		AND a.userId = #{userId}
	
	
		AND a.orderStatus = #{orderStatus}
	
	
		AND a.payStatus = #{payStatus}
	
	
		AND a.orderType = #{orderType}
	
	
		AND b.user_name = #{userName}
	
	
		AND a.payTime >= STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')
	
	
		AND a.payTime <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')

SELECT a.orderId orderid, a.orderCode ordercode, a.userId userid, a.qty qty, a.totalPrice totalprice, date_format(a.createTime,'%Y-%m-%d %H:%i:%s') createtime, a.orderStatus orderstatus,a.payStatus paystatus, date_format(a.payTime,'%Y-%m-%d %H:%i:%s') paytime,a.orderType ordertype,a.addressId addressid,a.awardRemark awardRemark, a.goodsprice goodsprice, a.giftAmount, a.salqty, a.awardStatus awardStatus, a.alipayStatus alipayStatus, c.userName trueName,CONCAT(c.address,c.addressDetails) address, c.userPhone phone FROM ct_order a LEFT JOIN ct_user b ON a.userid = b.id left join ct_order_address c on c.addressId = a.addressId
AND a.userId = #{userId}
AND a.orderStatus = #{orderStatus}
AND a.payStatus = #{payStatus}
AND a.orderType = #{orderType}
AND b.true_name = #{trueName}
AND b.user_name = #{userName}
AND a.createTime >= STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')
AND a.createTime <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')
order by a.createTime desc

SELECT a.orderId orderid, a.orderCode ordercode, a.userId userid, a.qty qty, a.totalPrice totalprice, date_format(a.createTime,'%Y-%m-%d %H:%i:%s') createtime, a.orderStatus orderstatus,a.payStatus paystatus, a.goodsprice goodsprice, a.giftAmount, date_format(a.payTime,'%Y-%m-%d %H:%i:%s') paytime,a.orderType,d.remark, ordertype,a.addressId addressid,a.awardRemark awardRemark, a.salqty salqty, a.alipayStatus alipayStatus, c.userName trueName, c.userPhone phone, CONCAT(c.address,c.addressDetails) address,d.orderDetailId, d.orderId, d.goods_id, d.quantity, d.price FROM ct_order a left join ct_user b ON a.userid = b.id left join ct_order_address c on c.addressId = a.addressId left join ct_orderdetail d on a.orderId = d.orderId
AND a.userId = #{userId}
AND a.orderStatus = #{orderStatus}
AND a.payStatus = #{payStatus}
AND a.orderType = #{orderType}
AND b.true_name = #{trueName}
AND b.user_name = #{userName}
AND a.createTime >= STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')
AND a.createTime <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')

SELECT sum(qty) as qty
FROM ct_order a LEFT JOIN ct_user b ON a.userid
= b.id

	
		AND a.userId = #{userId}
	
	
		AND a.orderStatus = #{orderStatus}
	
	
		AND a.payStatus = #{payStatus}
	
	
		AND a.orderType = #{orderType}
	
	
		AND b.user_name = #{userName}
	
	
		AND a.createTime >= STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')
	
	
		AND a.createTime <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')

		SELECT SUM(tt.goodsprice) as Totalperformance 
		from (select a.goodsprice
			from ct_order a  left join (
				select userId from ct_user_referee where refereeid = #{userId}
			)rf on a.userId = rf.userId	
            where a.payStatus=1
			union all
			select a.goodsprice
			from ct_order a where a.userId = #{userId} and a.payStatus=1
		)as tt

		select a.*
			from ct_order a  left join ct_orderdetail d on a.orderId = d.orderId 	
            where a.payStatus = #{payStatus}
			  and d.goods_id = #{goodsId}
			  and a.userId = #{userId}

select
	*
from ct_feihong_order

	
		AND userId = #{userid}
	
	
		AND orderId = #{orderid}
	
		AND orderStatus = '1'
		AND endDate >= #{currentDate}
		AND startDate <=  #{currentDate}
		AND ifnull(lastRunDate,startDate)  <  #{currentDate}

mapper.update:
update
ct_feihong_order
set orderStatus = #{orderstatus}, lastRunDate = #{lastrundate},totalFeiHongAmt=#{totalfeihongamt}
where orderId =
#{orderid,jdbcType=INTEGER}

update
ct_feihong_user
set orderAward = orderAward + #{orderaward}
where userId =
#{userid,jdbcType=INTEGER}

update
ct_feihong_user
set feiHongAmt = feiHongAmt + #{feihongamt}
where userId =
#{userid,jdbcType=INTEGER}
