mapper.select:
		SELECT * 
		  FROM tr_supplier
		   
			  
				AND id = #{id}
			
			  
				AND supplierName = #{supplierName}
			
			  
				AND province = #{province}
			
			  
				AND city = #{city}
			
			  
				AND businessNo = #{businessNo}
			
			  
				AND businessImage = #{businessImage}
			
			  
				AND cooperative = #{cooperative}
			
			  
				AND linkMan = #{linkMan}
			
			  
				AND linkPhone = #{linkPhone}
			
			  
				AND linkEmail = #{linkEmail}
			
			  
				AND status = #{status}
			
			  
				AND createTime = #{createTime}
			
			  
				AND auditTime = #{auditTime}
			
			  
				AND auditInfo = #{auditInfo}
			
			  
				AND remark = #{remark}

