mapper.select:
SELECT u.*,u1.user_name AS refereeUserName,a.* FROM
ct_user u LEFT JOIN
ct_user u1 ON u.refereeid = u1.id LEFT JOIN
ct_user_address a on u.id=
a.userId

	
		AND u.user_name like concat('', #{userName}, '%')
	
	
		AND u.true_name like concat('', #{trueName}, '%')
	
	
		AND u.mobile like concat('', #{mobile}, '%')
	
	
		AND u.user_level = #{userLevel}
	
	
		AND a.address = #{address}
	
	
		AND  u.reg_time >= #{startDate} 
	
	
		AND  u.reg_time <= #{endDate}

SELECT sum(u.baodan_amount) as amount FROM ct_user u
AND u.user_name like concat('', #{userName}, '%')
AND u.true_name like concat('', #{trueName}, '%')
AND u.mobile like concat('', #{mobile}, '%')
AND u.reg_time >= #{startDate}
AND u.reg_time <= #{endDate}

