mapper.insert:
insert into ct_user_parent (userid, parentid, distance,
position, network, lr_district
)
values (#{userid,jdbcType=INTEGER}, #{parentid,jdbcType=INTEGER}, #{distance,jdbcType=INTEGER},
#{position,jdbcType=VARCHAR}, #{network,jdbcType=BIT}, #{lrDistrict,jdbcType=TINYINT}
)

mapper.select:
select * from ( SELECT b.id, b.user_name AS user_name, b.total_performance, b.referee_number, b.user_level, b.reg_time, b.baodan_amount, b.isActivated, a.distance, a.lr_district, a.position, a.parentid, 0 as lr_amount, 0 as lf_amount FROM `ct_user_parent` a LEFT JOIN ct_user b ON a.userid =b.id
and a.parentid = #{parentId}
and a.distance <= #{distance}
UNION ALL SELECT id, user_name AS user_name, total_performance, referee_number, user_level, reg_time, baodan_amount, isActivated, 0 as distance, 0 as lr_district, 0 as position, #{parentId} as parentid, 0 as lr_amount, 0 as lf_amount from ct_user where id = #{parentId} )aa order by aa.distance, aa.`lr_district`

SELECT SUM(totalMoney) AS yj, a.parentid AS yj_userId 
FROM ct_user_parent a LEFT JOIN ct_user_static b ON a.userid = b.userid AND b.type=1
WHERE a.parentid = #{parentid} and a.lr_district=#{lr_district}

select

from ct_user_parent u
 
     
       AND u.userid = #{userid}
   
     
       AND u.parentid = #{parentid}
   
     
       AND u.lr_district = #{lrDistrict}
   
     
       AND u.distance = #{distance}
