mapper.insert:
insert into ct_user_referee (userid, refereeid,
distance
)
values (#{userid,jdbcType=INTEGER},
#{refereeid,jdbcType=INTEGER}, #{distance,jdbcType=INTEGER}
)

mapper.select:
select

from ct_user_referee u

	
		AND u.userid = #{userid}
	
	
		AND u.refereeid = #{refereeid}
	
	
		AND u.distance = #{distance}
	
	
	        AND u.distance <= '${maxDistance}'

SELECT CONCAT(b.true_name,'[',b.user_name,']') AS user_name, b.total_performance, b.referee_number, b.user_level, b.reg_time, a.userid, a.refereeid FROM `ct_user_referee` a LEFT JOIN ct_user b ON a.userid =b.id
a.refereeid = #{refereeId}
and a.distance <= #{distance}
order by a.distance LIMIT #{startRow},#{pageSize}

SELECT b.user_name AS user_name, IFNULL((SELECT amount FROM ct_user_account WHERE accountType='current' AND userId=b.id),0) AS currentAmount, IFNULL((SELECT amount FROM ct_user_account WHERE accountType='point' AND userId=b.id),0) AS pointAmount, IFNULL((SELECT amount FROM ct_user_account WHERE accountType='original' AND userId=b.id),0) AS originalAmount, (SELECT CONCAT(true_name,'[',user_name,']') FROM ct_user WHERE id=b.refereeid) AS refereeName, (SELECT CONCAT(true_name,'[',user_name,']') FROM ct_user WHERE id=b.parentid) AS parentidName, b.balance_dongjie, b.activation_time, (SELECT `name` FROM t_loan_dict_dtl WHERE dictId=1 AND `code`=b.user_level) AS levelName
FROM `ct_user_referee` a LEFT JOIN ct_user b ON a.userid =b.id
a.refereeid = #{refereeId}
and a.distance <= #{distance}
and b.reg_time >= #{startTime}
and b.reg_time <= #{endTime}
and b.baodan_amount is not null
and b.baodan_amount is null
order by a.distance LIMIT #{offset},#{rows}
